namespace Shine
{
	/** 删除角色消息(generated by shine) */
	export class DeletePlayerRequest extends LoginRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.DeletePlayer;
		
		/** 删除当前角色 */
		public playerID:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.DeletePlayer;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "DeletePlayerRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.playerID);
			
		}
		
		/** 创建实例 */
		public static createDeletePlayer(playerID:number):DeletePlayerRequest
		{
			var re:DeletePlayerRequest=new DeletePlayerRequest();
			re.playerID=playerID;
			return re;
		}
		
	}
}
