namespace Shine
{
	/** 角色登录消息(generated by shine) */
	export class PlayerLoginRequest extends LoginRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.PlayerLogin;
		
		/** 角色ID */
		public playerID:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.PlayerLogin;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "PlayerLoginRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.playerID);
			
		}
		
		/** 创建实例 */
		public static createPlayerLogin(playerID:number):PlayerLoginRequest
		{
			var re:PlayerLoginRequest=new PlayerLoginRequest();
			re.playerID=playerID;
			return re;
		}
		
	}
}
