namespace Shine
{
	/** 角色切换game服消息(generated by shine) */
	export class PlayerSwitchGameRequest extends LoginRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.PlayerSwitchGame;
		
		/** 令牌 */
		public token:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.PlayerSwitchGame;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "PlayerSwitchGameRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.token);
			
		}
		
		/** 创建实例 */
		public static createPlayerSwitchGame(token:number):PlayerSwitchGameRequest
		{
			var re:PlayerSwitchGameRequest=new PlayerSwitchGameRequest();
			re.token=token;
			return re;
		}
		
	}
}
