namespace Shine
{
	/** 客户端发送邮件消息(generated by shine) */
	export class ClientSendMailRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.ClientSendMail;
		
		/** 目标玩家ID */
		public playerID:number;
		
		/** 邮件数据 */
		public mail:MailData;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.ClientSendMail;
		}
		
		protected copyData():void
		{
			super.copyData();
			var mailTemp:MailData=this.mail;
			this.mail=mailTemp.clone() as MailData;
			
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "ClientSendMailRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.playerID);
			
			stream.writeDataSimpleNotNull(this.mail);
			
		}
		
		/** 创建实例 */
		public static createClientSendMail(playerID:number,mail:MailData):ClientSendMailRequest
		{
			var re:ClientSendMailRequest=new ClientSendMailRequest();
			re.playerID=playerID;
			re.mail=mail;
			return re;
		}
		
	}
}
