namespace Shine
{
	/** 邮件已读消息(generated by shine) */
	export class MailReadedRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.MailReaded;
		
		/** 邮件序号 */
		public index:number;
		
		/** 邮件实例ID */
		public instanceID:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.MailReaded;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "MailReadedRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.index);
			
			stream.writeInt(this.instanceID);
			
		}
		
		/** 创建实例 */
		public static createMailReaded(index:number,instanceID:number):MailReadedRequest
		{
			var re:MailReadedRequest=new MailReadedRequest();
			re.index=index;
			re.instanceID=instanceID;
			return re;
		}
		
	}
}
