namespace Shine
{
	/** 领取邮件内容消息(generated by shine) */
	export class TakeMailRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.TakeMail;
		
		/** 邮件序号 */
		public index:number;
		
		/** 邮件实例ID */
		public instanceID:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.TakeMail;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "TakeMailRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.index);
			
			stream.writeInt(this.instanceID);
			
		}
		
		/** 创建实例 */
		public static createTakeMail(index:number,instanceID:number):TakeMailRequest
		{
			var re:TakeMailRequest=new TakeMailRequest();
			re.index=index;
			re.instanceID=instanceID;
			return re;
		}
		
	}
}
