namespace Shine
{
	/** 接任务消息(generated by shine) */
	export class AcceptQuestRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.AcceptQuest;
		
		/** 任务id */
		public id:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.AcceptQuest;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "AcceptQuestRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.id);
			
		}
		
		/** 创建实例 */
		public static createAcceptQuest(id:number):AcceptQuestRequest
		{
			var re:AcceptQuestRequest=new AcceptQuestRequest();
			re.id=id;
			return re;
		}
		
	}
}
