namespace Shine
{
	/** 交任务消息(generated by shine) */
	export class CommitQuestRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.CommitQuest;
		
		/** 任务id */
		public id:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.CommitQuest;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "CommitQuestRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.id);
			
		}
		
		/** 创建实例 */
		public static createCommitQuest(id:number):CommitQuestRequest
		{
			var re:CommitQuestRequest=new CommitQuestRequest();
			re.id=id;
			return re;
		}
		
	}
}
