namespace Shine
{
	/** 获取成就奖励消息(generated by shine) */
	export class GetAchievementRewardRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.GetAchievementReward;
		
		/** id */
		public id:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.GetAchievementReward;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "GetAchievementRewardRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.id);
			
		}
		
		/** 创建实例 */
		public static createGetAchievementReward(id:number):GetAchievementRewardRequest
		{
			var re:GetAchievementRewardRequest=new GetAchievementRewardRequest();
			re.id=id;
			return re;
		}
		
	}
}
