namespace Shine
{
	/** 控制单位操作消息(generated by shine) */
	export class CUnitRRequest extends SceneRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.CUnitR;
		
		/** 实例ID */
		public instanceID:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.CUnitR;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "CUnitRRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.instanceID);
			
		}
		
		/** 创建实例 */
		public static createCUnitR(instanceID:number):CUnitRRequest
		{
			var re:CUnitRRequest=new CUnitRRequest();
			re.instanceID=instanceID;
			return re;
		}
		
	}
}
