namespace Shine
{
	/** 帧同步单个玩家消息(generated by shine) */
	export class FrameSyncOneRequest extends SceneRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.FrameSyncOne;
		
		/** 指令 */
		public command:FrameSyncCommandData;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.FrameSyncOne;
		}
		
		protected copyData():void
		{
			super.copyData();
			var commandTemp:FrameSyncCommandData=this.command;
			this.command=commandTemp.clone() as FrameSyncCommandData;
			
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "FrameSyncOneRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeDataSimpleNotNull(this.command);
			
		}
		
		/** 创建实例 */
		public static createFrameSyncOne(command:FrameSyncCommandData):FrameSyncOneRequest
		{
			var re:FrameSyncOneRequest=new FrameSyncOneRequest();
			re.command=command;
			return re;
		}
		
	}
}
