namespace Shine
{
	/** 客户端单位移动到点消息(generated by shine) */
	export class CUnitMovePosRequest extends CUnitRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.CUnitMovePos;
		
		/** 移动类型 */
		public type:number;
		
		/** 当前位置 */
		public nowPos:PosData;
		
		/** 目标位置 */
		public targetPos:PosData;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.CUnitMovePos;
		}
		
		protected copyData():void
		{
			super.copyData();
			var nowPosTemp:PosData=this.nowPos;
			if(nowPosTemp!=null)
			{
				this.nowPos=new PosData();
				this.nowPos.copy(nowPosTemp);
			}
			else
			{
				this.nowPos=null;
			}
			
			var targetPosTemp:PosData=this.targetPos;
			this.targetPos=new PosData();
			this.targetPos.copy(targetPosTemp);
			
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "CUnitMovePosRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.type);
			
			if(this.nowPos!=null)
			{
				stream.writeBoolean(true);
				this.nowPos.writeBytesSimple(stream);
			}
			else
			{
				stream.writeBoolean(false);
			}
			
			this.targetPos.writeBytesSimple(stream);
			
		}
		
		/** 创建实例 */
		public static createCUnitMovePos(instanceID:number,type:number,nowPos:PosData,targetPos:PosData):CUnitMovePosRequest
		{
			var re:CUnitMovePosRequest=new CUnitMovePosRequest();
			re.instanceID=instanceID;
			re.type=type;
			re.nowPos=nowPos;
			re.targetPos=targetPos;
			return re;
		}
		
	}
}
