namespace Shine
{
	/** 单位操作消息(generated by shine) */
	export class CUnitOperateRequest extends CUnitRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.CUnitOperate;
		
		/** 目标操作体实例ID */
		public targetInstanceID:number;
		
		/** 参数 */
		public arg:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.CUnitOperate;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.targetInstanceID);
			
			stream.writeInt(this.arg);
			
		}
		
		/** 创建实例 */
		public static createCUnitOperate(instanceID:number,targetInstanceID:number,arg:number):CUnitOperateRequest
		{
			var re:CUnitOperateRequest=new CUnitOperateRequest();
			re.instanceID=instanceID;
			re.targetInstanceID=targetInstanceID;
			re.arg=arg;
			return re;
		}
		
	}
}
