namespace Shine
{
	/** 拾取掉落物品包全部消息(generated by shine) */
	export class CUnitPickUpItemBagAllRequest extends CUnitRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.CUnitPickUpItemBagAll;
		
		/** 目标物品实例ID */
		public targetInstanceID:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.CUnitPickUpItemBagAll;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.targetInstanceID);
			
		}
		
		/** 创建实例 */
		public static createCUnitPickUpItemBagAll(instanceID:number,targetInstanceID:number):CUnitPickUpItemBagAllRequest
		{
			var re:CUnitPickUpItemBagAllRequest=new CUnitPickUpItemBagAllRequest();
			re.instanceID=instanceID;
			re.targetInstanceID=targetInstanceID;
			return re;
		}
		
	}
}
