namespace Shine
{
	/** 客户端控制单位子弹结束(isClientDrivePosAndAttack下才有意义)(generated by shine) */
	export class CUnitRemoveBulletRequest extends CUnitRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.CUnitRemoveBullet;
		
		/** 子弹实例ID */
		public bulletInstanceID:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.CUnitRemoveBullet;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "CUnitRemoveBulletRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.bulletInstanceID);
			
		}
		
		/** 创建实例 */
		public static createCUnitRemoveBullet(instanceID:number,bulletInstanceID:number):CUnitRemoveBulletRequest
		{
			var re:CUnitRemoveBulletRequest=new CUnitRemoveBulletRequest();
			re.instanceID=instanceID;
			re.bulletInstanceID=bulletInstanceID;
			return re;
		}
		
	}
}
