namespace Shine
{
	/** 控制单位特殊移动(generated by shine) */
	export class CUnitSpecialMoveRequest extends CUnitRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.CUnitSpecialMove;
		
		/** 特殊移动ID */
		public id:number;
		
		/** 当前位置 */
		public posDir:PosDirData;
		
		/** 参数组 */
		public args:number[];
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.CUnitSpecialMove;
		}
		
		protected copyData():void
		{
			super.copyData();
			var posDirTemp:PosDirData=this.posDir;
			if(posDirTemp!=null)
			{
				this.posDir=new PosDirData();
				this.posDir.copy(posDirTemp);
			}
			else
			{
				this.posDir=null;
			}
			
			var argsTemp:number[]=this.args;
			if(argsTemp!=null)
			{
				var argsR:number[]=argsTemp;
				var argsLen:number=argsR.length;
				this.args=new Array<number>(argsLen);
				var argsT:number[]=this.args;
				for(var argsI:number=0;argsI<argsLen;++argsI)
				{
					var argsV:number=argsR[argsI];
					var argsU:number;
					argsU=argsV;
					
					argsT[argsI]=argsU;
				}
			}
			else
			{
				this.args=null;
			}
			
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "CUnitSpecialMoveRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.id);
			
			if(this.posDir!=null)
			{
				stream.writeBoolean(true);
				this.posDir.writeBytesSimple(stream);
			}
			else
			{
				stream.writeBoolean(false);
			}
			
			if(this.args!=null)
			{
				stream.writeBoolean(true);
				var argsT:number[]=this.args;
				stream.writeLen(argsT.length);
				for(var argsVI=0,argsVLen=argsT.length;argsVI<argsVLen;++argsVI)
				{
					var argsV:number=argsT[argsVI];
					stream.writeInt(argsV);
					
				}
			}
			else
			{
				stream.writeBoolean(false);
			}
			
		}
		
		/** 创建实例 */
		public static createCUnitSpecialMove(instanceID:number,id:number,posDir:PosDirData,args:number[]):CUnitSpecialMoveRequest
		{
			var re:CUnitSpecialMoveRequest=new CUnitSpecialMoveRequest();
			re.instanceID=instanceID;
			re.id=id;
			re.posDir=posDir;
			re.args=args;
			return re;
		}
		
	}
}
