namespace Shine
{
	/** 玩家单位使用技能拓展(generated by shine) */
	export class CUnitUseSkillExRequest extends CUnitUseSkillRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.CUnitUseSkillEx;
		
		/** 使用技能ID */
		public useSkillID:number;
		
		/** 随机种子序号 */
		public seedIndex:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.CUnitUseSkillEx;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "CUnitUseSkillExRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.useSkillID);
			
			stream.writeInt(this.seedIndex);
			
		}
		
		/** 创建实例 */
		public static createCUnitUseSkillEx(instanceID:number,skillID:number,targetData:SkillTargetData,posDir:PosDirData,useSkillID:number,seedIndex:number):CUnitUseSkillExRequest
		{
			var re:CUnitUseSkillExRequest=new CUnitUseSkillExRequest();
			re.instanceID=instanceID;
			re.skillID=skillID;
			re.targetData=targetData;
			re.posDir=posDir;
			re.useSkillID=useSkillID;
			re.seedIndex=seedIndex;
			return re;
		}
		
	}
}
