namespace Shine
{
	/** 客户端查询角色消息(generated by shine) */
	export class QueryPlayerRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.QueryPlayer;
		
		/** 目标角色ID */
		public playerID:number;
		
		/** 查询类型 */
		public type:number;
		
		/** 查询参数 */
		public args:number[];
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.QueryPlayer;
		}
		
		protected copyData():void
		{
			super.copyData();
			var argsTemp:number[]=this.args;
			if(argsTemp!=null)
			{
				var argsR:number[]=argsTemp;
				var argsLen:number=argsR.length;
				this.args=new Array<number>(argsLen);
				var argsT:number[]=this.args;
				for(var argsI:number=0;argsI<argsLen;++argsI)
				{
					var argsV:number=argsR[argsI];
					var argsU:number;
					argsU=argsV;
					
					argsT[argsI]=argsU;
				}
			}
			else
			{
				this.args=null;
			}
			
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "QueryPlayerRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.playerID);
			
			stream.writeInt(this.type);
			
			if(this.args!=null)
			{
				stream.writeBoolean(true);
				var argsT:number[]=this.args;
				stream.writeLen(argsT.length);
				for(var argsVI=0,argsVLen=argsT.length;argsVI<argsVLen;++argsVI)
				{
					var argsV:number=argsT[argsVI];
					stream.writeInt(argsV);
					
				}
			}
			else
			{
				stream.writeBoolean(false);
			}
			
		}
		
		/** 创建实例 */
		public static createQueryPlayer(playerID:number,type:number,args:number[]):QueryPlayerRequest
		{
			var re:QueryPlayerRequest=new QueryPlayerRequest();
			re.playerID=playerID;
			re.type=type;
			re.args=args;
			return re;
		}
		
	}
}
