namespace Shine
{
	/** 玩家聊天消息(generated by shine) */
	export class PlayerChatRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.PlayerChat;
		
		/** 聊天数据 */
		public data:ChatData;
		
		/** 频道 */
		public channel:number;
		
		/** 二级索引 */
		public key:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.PlayerChat;
		}
		
		protected copyData():void
		{
			super.copyData();
			var dataTemp:ChatData=this.data;
			this.data=dataTemp.clone() as ChatData;
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeDataSimpleNotNull(this.data);
			
			stream.writeInt(this.channel);
			
			stream.writeLong(this.key);
			
		}
		
		/** 创建实例 */
		public static createPlayerChat(data:ChatData,channel:number,key:number):PlayerChatRequest
		{
			var re:PlayerChatRequest=new PlayerChatRequest();
			re.data=data;
			re.channel=channel;
			re.key=key;
			return re;
		}
		
	}
}
