namespace Shine
{
	/** 添加所有好友消息(generated by shine) */
	export class AddAllFriendRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.AddAllFriend;
		
		/** 角色ID */
		public playerIDList:SList<number>;
		
		/** 添加方式(g层定义) */
		public type:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.AddAllFriend;
		}
		
		protected copyData():void
		{
			super.copyData();
			var playerIDListTemp:SList<number>=this.playerIDList;
			this.playerIDList=new SList<number>();
			var playerIDListT:SList<number>=this.playerIDList;
			if(!playerIDListTemp.isEmpty())
			{
				for(var playerIDListVI=0,playerIDListVLen=playerIDListTemp.length;playerIDListVI<playerIDListVLen;++playerIDListVI)
				{
					var playerIDListV:number=playerIDListTemp[playerIDListVI];
					var playerIDListU:number;
					playerIDListU=playerIDListV;
					
					playerIDListT.add(playerIDListU);
				}
			}
			
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "AddAllFriendRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLen(this.playerIDList.size());
			if(!this.playerIDList.isEmpty())
			{
				for(var playerIDListVI=0,playerIDListVLen=this.playerIDList.length;playerIDListVI<playerIDListVLen;++playerIDListVI)
				{
					var playerIDListV:number=this.playerIDList[playerIDListVI];
					stream.writeLong(playerIDListV);
					
				}
			}
			
			stream.writeInt(this.type);
			
		}
		
		/** 创建实例 */
		public static createAddAllFriend(playerIDList:SList<number>,type:number):AddAllFriendRequest
		{
			var re:AddAllFriendRequest=new AddAllFriendRequest();
			re.playerIDList=playerIDList;
			re.type=type;
			return re;
		}
		
	}
}
