namespace Shine
{
	/** 添加好友黑名单(generated by shine) */
	export class AddFriendBlackListRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.AddFriendBlackList;
		
		public playerID:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.AddFriendBlackList;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "AddFriendBlackListRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.playerID);
			
		}
		
		/** 创建实例 */
		public static createAddFriendBlackList(playerID:number):AddFriendBlackListRequest
		{
			var re:AddFriendBlackListRequest=new AddFriendBlackListRequest();
			re.playerID=playerID;
			return re;
		}
		
	}
}
