namespace Shine
{
	/** 添加好友消息(generated by shine) */
	export class AddFriendRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.AddFriend;
		
		/** 角色ID */
		public playerID:number;
		
		/** 添加方式(g层定义) */
		public type:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.AddFriend;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "AddFriendRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.playerID);
			
			stream.writeInt(this.type);
			
		}
		
		/** 创建实例 */
		public static createAddFriend(playerID:number,type:number):AddFriendRequest
		{
			var re:AddFriendRequest=new AddFriendRequest();
			re.playerID=playerID;
			re.type=type;
			return re;
		}
		
	}
}
