namespace Shine
{
	/** 同意添加好友消息(generated by shine) */
	export class AgreeAddFriendRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.AgreeAddFriend;
		
		/** 同意添加好友消息 */
		public playerID:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.AgreeAddFriend;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "AgreeAddFriendRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.playerID);
			
		}
		
		/** 创建实例 */
		public static createAgreeAddFriend(playerID:number):AgreeAddFriendRequest
		{
			var re:AgreeAddFriendRequest=new AgreeAddFriendRequest();
			re.playerID=playerID;
			return re;
		}
		
	}
}
