namespace Shine
{
	/** 拒绝好友申请(generated by shine) */
	export class RefuseApplyAddFriendRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.RefuseApplyAddFriend;
		
		/** 角色ID */
		public playerID:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.RefuseApplyAddFriend;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "RefuseApplyAddFriendRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.playerID);
			
		}
		
		/** 创建实例 */
		public static createRefuseApplyAddFriend(playerID:number):RefuseApplyAddFriendRequest
		{
			var re:RefuseApplyAddFriendRequest=new RefuseApplyAddFriendRequest();
			re.playerID=playerID;
			return re;
		}
		
	}
}
