namespace Shine
{
	/** 客户端到中心服转发到game消息(generated by shine) */
	export class CenterTransClientToGameRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.CenterTransClientToGame;
		
		/** 绑定消息 */
		private _request:CenterRequest;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.CenterTransClientToGame;
			this.setDontCopy();
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "CenterTransClientToGameRequest";
		}
		
		protected doWriteBytesSimple(stream:BytesWriteStream):void
		{
			if(this._request==null)
			{
				Ctrl.throwError("绑定消息为空");
				return;
			}

			this._request.writeToStream(stream);
		}
		
		public setData(request:CenterRequest):void
		{
			this._request=request;
		}
		
		/** 创建实例 */
		public static createCenterTransClientToGame():CenterTransClientToGameRequest
		{
			var re:CenterTransClientToGameRequest=new CenterTransClientToGameRequest();
			return re;
		}
		
	}
}
