namespace Shine
{
	/** 保存bool(generated by shine) */
	export class SaveBooleanRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.SaveBoolean;
		
		/** key */
		public key:number;
		
		/** 值 */
		public value:boolean;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.SaveBoolean;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SaveBooleanRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.key);
			
			stream.writeBoolean(this.value);
			
		}
		
		/** 创建实例 */
		public static createSaveBoolean(key:number,value:boolean):SaveBooleanRequest
		{
			var re:SaveBooleanRequest=new SaveBooleanRequest();
			re.key=key;
			re.value=value;
			return re;
		}
		
	}
}
