namespace Shine
{
	/** 保存long型(generated by shine) */
	export class SaveLongRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.SaveLong;
		
		/** key */
		public key:number;
		
		/** 值 */
		public value:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.SaveLong;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SaveLongRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.key);
			
			stream.writeLong(this.value);
			
		}
		
		/** 创建实例 */
		public static createSaveLong(key:number,value:number):SaveLongRequest
		{
			var re:SaveLongRequest=new SaveLongRequest();
			re.key=key;
			re.value=value;
			return re;
		}
		
	}
}
