namespace Shine
{
	/** 保存string消息(generated by shine) */
	export class SaveStringRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.SaveString;
		
		/** key */
		public key:string;
		
		/** 值 */
		public value:string;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.SaveString;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SaveStringRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeUTF(this.key);
			
			stream.writeUTF(this.value);
			
		}
		
		/** 创建实例 */
		public static createSaveString(key:string,value:string):SaveStringRequest
		{
			var re:SaveStringRequest=new SaveStringRequest();
			re.key=key;
			re.value=value;
			return re;
		}
		
	}
}
