namespace Shine
{
	/** 活动重置(generated by shine) */
	export class ActivityResetResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.ActivityReset;
		
		public id:number;
		
		/** 是否到时间自然触发 */
		public atTime:boolean;
		
		/** 下个重置时间 */
		public nextTime:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.ActivityReset;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "ActivityResetResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.nextTime=stream.readLong();
			
			this.atTime=stream.readBoolean();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.activity.doActivityReset(this.id,this.nextTime,this.atTime);
		}
		
	}
}
