namespace Shine
{
	/** 活动开启/关闭消息(generated by shine) */
	export class ActivitySwitchResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.ActivitySwitch;
		
		/** 活动ID */
		public id:number;
		
		/** 是否开启 */
		public isRunning:boolean;
		
		/** 上次触发时间 */
		public lastTurnTime:number;
		
		/** 下次触发时间 */
		public nextTurnTime:number;
		
		/** 是否到时间自然触发 */
		public atTime:boolean;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.ActivitySwitch;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "ActivitySwitchResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.isRunning=stream.readBoolean();
			
			this.lastTurnTime=stream.readLong();
			
			this.nextTurnTime=stream.readLong();
			
			this.atTime=stream.readBoolean();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			if(this.isRunning)
				this.me.activity.doActivityOpen(this.id,this.lastTurnTime,this.nextTurnTime,this.atTime);
			else
				this.me.activity.doActivityClose(this.id,this.lastTurnTime,this.nextTurnTime,this.atTime);
		}
		
	}
}
