namespace Shine
{
	/** 添加格子数(generated by shine) */
	export class FuncAddItemGridNumResponse extends FuncSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.FuncAddItemGridNum;
		
		/** 格子数 */
		public gridNum:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.FuncAddItemGridNum;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "FuncAddItemGridNumResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.gridNum=stream.readInt();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.func.getItemContainerTool(this.funcID).onAddGridNumByServer(this.gridNum);
		}
		
	}
}
