namespace Shine
{
	/** 推送添加单个物品(generated by shine) */
	export class FuncAddOneItemResponse extends FuncSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.FuncAddOneItem;
		
		/** 途径 */
		public way:number;
		
		/** 序号 */
		public index:number;
		
		/** 物品数据 */
		public item:ItemData;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.FuncAddOneItem;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "FuncAddOneItemResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.way=stream.readInt();
			
			this.index=stream.readInt();
			
			this.item=stream.readDataSimpleNotNull() as ItemData;
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.func.getBaseItemContainerTool(this.funcID).onAddItemByServer(this.index,this.item,this.way);
		}
		
	}
}
