namespace Shine
{
	/** 移除物品消息(generated by shine) */
	export class FuncRemoveItemResponse extends FuncSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.FuncRemoveItem;
		
		/** 途径 */
		public way:number;
		
		/** 移除序号数目组 */
		public dic:SMap<number,number>;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.FuncRemoveItem;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "FuncRemoveItemResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.way=stream.readInt();
			
			var dicLen:number=stream.readLen();
			if(this.dic!=null)
			{
				this.dic.clear();
				this.dic.ensureCapacity(dicLen);
			}
			else
			{
				this.dic=new SMap<number,number>(dicLen);
			}
			
			var dicT:SMap<number,number>=this.dic;
			for(var dicI:number=dicLen-1;dicI>=0;--dicI)
			{
				var dicK:number;
				var dicV:number;
				dicK=stream.readInt();
				
				dicV=stream.readInt();
				
				dicT.put(dicK,dicV);
			}
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.func.getBaseItemContainerTool(this.funcID).onRemoveItemsByServer(this.dic,this.way);
		}
		
	}
}
