namespace Shine
{
	/** 移除单个物品(generated by shine) */
	export class FuncRemoveOneItemResponse extends FuncSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.FuncRemoveOneItem;
		
		/** 途径 */
		public way:number;
		
		/** 序号 */
		public index:number;
		
		/** 数目 */
		public num:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.FuncRemoveOneItem;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "FuncRemoveOneItemResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.way=stream.readInt();
			
			this.index=stream.readInt();
			
			this.num=stream.readInt();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.func.getBaseItemContainerTool(this.funcID).onRemoveItemByServer(this.index,this.num,this.way);
		}
		
	}
}
