namespace Shine
{
	/** 使用物品结果消息(generated by shine) */
	export class FuncUseItemResultResponse extends FuncSResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.FuncUseItemResult;
		
		/** 道具ID(使用的) */
		public itemID:number;
		
		/** 数目 */
		public num:number;
		
		/** 结果 */
		public result:boolean;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.FuncUseItemResult;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "FuncUseItemResultResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.itemID=stream.readInt();
			
			this.num=stream.readInt();
			
			this.result=stream.readBoolean();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.func.getBaseItemContainerTool(this.funcID).onUseItemResult(this.itemID,this.num,this.result);
		}
		
	}
}
