namespace Shine
{
	/** 创建角色成功消息(generated by shine) */
	export class CreatePlayerSuccessResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.CreatePlayerSuccess;
		
		public role:PlayerLoginData;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.CreatePlayerSuccess;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "CreatePlayerSuccessResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.role=stream.readDataSimpleNotNull() as PlayerLoginData;
			
		}
		
		/** 执行 */
		protected execute():void
		{
			GameC.main.onCreatePlayerSuccess(this.role);
		}
		
	}
}
