namespace Shine
{
	/** 回复角色列表消息(generated by shine) */
	export class RePlayerListResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.RePlayerList;
		
		/** 角色列表 */
		public roles:SList<PlayerLoginData>;
		
		/** 服务器出生码 */
		public serverBornCode:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.RePlayerList;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "RePlayerListResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			var rolesLen:number=stream.readLen();
			if(this.roles!=null)
			{
				this.roles.clear();
				this.roles.ensureCapacity(rolesLen);
			}
			else
			{
				this.roles=new SList<PlayerLoginData>();
			}
			
			var rolesT:SList<PlayerLoginData>=this.roles;
			for(var rolesI:number=rolesLen-1;rolesI>=0;--rolesI)
			{
				var rolesV:PlayerLoginData;
				rolesV=stream.readDataSimpleNotNull() as PlayerLoginData;
				
				rolesT.add(rolesV);
			}
			
			this.serverBornCode=stream.readInt();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			GameC.main.onRePlayerList(this.roles,this.serverBornCode);
		}
		
	}
}
