namespace Shine
{
	/** 推送绑定平台消息(generated by shine) */
	export class SendBindPlatformResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.SendBindPlatform;
		
		/** uid */
		public uid:string;
		
		/** 平台 */
		public platform:string;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.SendBindPlatform;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SendBindPlatformResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.uid=stream.readUTF();
			
			this.platform=stream.readUTF();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			
		}
		
	}
}
