namespace Shine
{
	/** 切换game服消息(generated by shine) */
	export class SwitchGameResponse extends GameResponse
	{
		/** 服务器目标 */
		public info:ClientLoginServerInfoData;
		
		/** 数据类型ID */
		public static dataID:number=GameResponseType.SwitchGame;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.SwitchGame;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SwitchGameResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.info=stream.readDataSimpleNotNull() as ClientLoginServerInfoData;
			
		}
		
		/** 执行 */
		protected execute():void
		{
			// GameC.main.onSwitchGame(this.token,this.host,this.port);
			GameC.main.onSwitchGame(this.info);
		}
		
	}
}
