namespace Shine
{
	/** 新邮件消息(generated by shine) */
	export class AddMailResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.AddMail;
		
		/** 数据 */
		public data:MailData;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.AddMail;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "AddMailResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.data=stream.readDataSimpleNotNull() as MailData;
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.mail.onAddMail(this.data);
		}
		
	}
}
