namespace Shine
{
	/** 回复获取全部邮件消息(generated by shine) */
	export class ReGetAllMailResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.ReGetAllMail;
		
		/** 数据 */
		public list:SList<MailData>;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.ReGetAllMail;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "ReGetAllMailResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			var listLen:number=stream.readLen();
			if(this.list!=null)
			{
				this.list.clear();
				this.list.ensureCapacity(listLen);
			}
			else
			{
				this.list=new SList<MailData>();
			}
			
			var listT:SList<MailData>=this.list;
			for(var listI:number=listLen-1;listI>=0;--listI)
			{
				var listV:MailData;
				listV=stream.readDataSimpleNotNull() as MailData;
				
				listT.add(listV);
			}
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.mail.onReGetAllMails(this.list);
		}
		
	}
}
