namespace Shine
{
	/** 领取邮件成功(generated by shine) */
	export class TakeMailSuccessResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.TakeMailSuccess;
		
		/** 序号 */
		public index:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.TakeMailSuccess;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "TakeMailSuccessResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.index=stream.readInt();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.mail.onTakeMail(this.index);
		}
		
	}
}
