namespace Shine
{
	/** 刷新目标数目消息(generated by shine) */
	export class RefreshTaskResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.RefreshTask;
		
		/** 实例ID */
		public instanceID:number;
		
		/** 数目 */
		public num:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.RefreshTask;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "RefreshTaskResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.instanceID=stream.readInt();
			
			this.num=stream.readInt();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.quest.onRefreshTask(this.instanceID,this.num);
		}
		
	}
}
