namespace Shine
{
	/** 推送添加成就消息(generated by shine) */
	export class SendAcceptAchievementResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.SendAcceptAchievement;
		
		/** 成就数据 */
		public data:AchievementData;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.SendAcceptAchievement;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SendAcceptAchievementResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.data=stream.readDataSimpleNotNull() as AchievementData;
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.achievement.onAcceptAchievementByServer(this.data);
		}
		
	}
}
