namespace Shine
{
	/** 推送接任务消息(generated by shine) */
	export class SendAcceptQuestResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.SendAcceptQuest;
		
		/** 任务数据 */
		public data:QuestData;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.SendAcceptQuest;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SendAcceptQuestResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.data=stream.readDataSimpleNotNull() as QuestData;
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.data.tasks.forEach(v => 
			{
				v.reloadConfig();
			});
			
			this.me.quest.onAcceptQuest(this.data);
		}
		
	}
}
