namespace Shine
{
	/** 推送任务失败消息(generated by shine) */
	export class SendQuestFailedResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.SendQuestFailed;
		
		/** 任务id */
		public id:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.SendQuestFailed;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SendQuestFailedResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.quest.onQuestFailed(this.id);
		}
		
	}
}
