namespace Shine
{
	/** 更改角色名字消息(generated by shine) */
	export class ChangeRoleNameResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.ChangeRoleName;
		
		/** 名字 */
		public name:string;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.ChangeRoleName;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "ChangeRoleNameResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.name=stream.readUTF();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.role.onChangeName(this.name);
		}
		
	}
}
