namespace Shine
{
	/** 刷新货币消息(generated by shine) */
	export class RefreshCurrencyResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.RefreshCurrency;
		
		/** 类型 */
		public type:number;
		
		/** 值 */
		public value:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.RefreshCurrency;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "RefreshCurrencyResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.type=stream.readInt();
			
			this.value=stream.readLong();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.role.onRefreshCurrency(this.type,this.value);
		}
		
	}
}
