namespace Shine
{
	/** 刷经验消息(generated by shine) */
	export class RefreshExpResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.RefreshExp;
		
		/** 经验值 */
		public exp:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.RefreshExp;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "RefreshExpResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.exp=stream.readLong();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.role.onRefreshExp(this.exp);
		}
		
	}
}
