namespace Shine
{
	/** 刷新角色战斗力消息(generated by shine) */
	export class RefreshFightForceResponse extends GameResponse
	{
		/** 数据类型ID */
		public static dataID:number=GameResponseType.RefreshFightForce;
		
		/** 战斗力 */
		public fightForce:number;
		
		constructor()
		{
			super();
			this._dataID=GameResponseType.RefreshFightForce;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "RefreshFightForceResponse";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.fightForce=stream.readLong();
			
		}
		
		/** 执行 */
		protected execute():void
		{
			this.me.role.onSetFightForceBeServer(this.fightForce);
		}
		
	}
}
